mapper.select:
SELECT * FROM metadata_table_info


        AND data_layer = #{query.layer}


        AND business_domain = #{query.businessDomain}


        AND owner = #{query.owner}


        AND (table_name LIKE CONCAT('%', #{query.search}, '%') 
             OR table_name_cn LIKE CONCAT('%', #{query.search}, '%')
             OR table_description LIKE CONCAT('%', #{query.search}, '%'))


ORDER BY created_time DESC
